#include <bits/stdc++.h>
using namespace std;

int main() {
    vector<int> arr;
    string n = "";

    for (char c : n) arr.push_back(c - '0');

    int left = 0, right = 0;
    long long product = 1;
    long long max_product = 0;

    while (right < arr.size()) {
        if (arr[right] == 0) {
            product = 1;
            left = right + 1;
            right = left;
        } else {
            product *= arr[right];
            if (right - left + 1 < 13) {
                right++;
            } else if (right - left + 1 == 13) {
                max_product = max(max_product, product);
                product /= arr[left]; 
                left++;
                right++;
            }
        }
    }

    cout << max_product << endl;
    return 0;
}
